[
  {
    "front": "Neuroscientists",
    "back": "The researchers discovered a brain circuit at MIT that controls vocalization in both humans and mice."
  },
  {
    "front": "Circuit in the brain",
    "back": "The circuit is found in brainstem region called the pre-B tzinger complex and regulates breathing and speech."
  },
  {
    "front": "Mice unable to produce Ultrasonic Vocalisations",
    "back": "When the researchers blocked the circuit in mice, they were unable to produce ultrasonic vocalisations (USVs) or any other kind of vocalisation."
  },
  {
    "front": "Overriding brain circuit",
    "back": "The circuit can be overridden, such as when mice produce USVs even when breathing in."
  },
  {
    "front": "Coughing and Swallowing",
    "back": "The team plans to study the circuit's role in functions such as coughing and swallowing."
  },
  {
    "front": "Pre-B Tzinger Complex",
    "back": "The Pre-B Tzinger complex is the brainstem region where the circuit is found"
  },
  {
    "front": "Regulates Breathing Rhythm",
    "back": "The complex regulates the breathing rhythm and stops speech when it is time to breathe in"
  }, 
  {
    "front": "Priority Breathing Over Speech",
    "back": "Neuroscientists believe breathing is prioritized over speaking"
  },
  {
    "front": "Mice and Human Vocalisation",
    "back": "The study compared vocalisation controls in both humans and mice to find similarities in the brain circuits"
  },
  {
    "front": "Ultrasonic Vocalisations",
    "back": "Ultrasonic vocalisations (USVs) are vocalisations produced by mice"
  },
  {
    "front": "Cognitive Functions",
    "back": "The study contributes to our understanding of basic cognitive functions"
  }
]
